import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { PaisesService } from '../paises.service';

@Component({
  selector: 'app-banderas',
  templateUrl: './banderas.component.html',
  styleUrls: ['./banderas.component.css']
})
export class BanderasComponent implements OnInit {
  paisEnRuta: string = '';
  datos: any;
  flagSymbol: string = '';
  borderCountries: string[] = [];
  countryData: any;

  constructor(private rutaActiva: ActivatedRoute, private service: PaisesService) {}

  ngOnInit(): void {
    this.paisEnRuta = this.rutaActiva.snapshot.params['pais'];
    this.service.dameDatos(`https://restcountries.com/v3.1/name/${this.paisEnRuta}?fullText=true`)
      .then((datos: any) => {
        console.log(datos);
        this.datos = datos[0];
        this.flagSymbol = this.datos.flags.svg;
        this.borderCountries = this.datos.borders;
        this.countryData = this.datos;
      });
  }
}
