import { Pipe, PipeTransform } from '@angular/core';
import { PaisesService } from './paises.service';

@Pipe({
  name: 'fronteraPais'
})
export class FronteraPaisPipe implements PipeTransform {

  constructor(private service: PaisesService) { }

  transform(value: Array<string>): Promise<Array<string>> {
    const promises = value.map((border: string) => {
      return this.service.dameDatos("https://restcountries.com/v3.1/alpha/" + border)
        .then((datos: any) => {
          return datos[0].name.common;
          console.log(datos.namme.common)
        })
    });
    return new Promise<Array<string>>((resolve, reject) => {
      Promise.all(promises).then((names) => {
        resolve(names);
      }).catch((error) => {
        reject(error);
      });
    });
  }

}
