import { Pipe, PipeTransform } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Pipe({
  name: 'FronteraPaiesePipe'
})
export class FronteraPaiesePipe implements PipeTransform {
  constructor(private http: HttpClient) {}

  transform(countryCode: string): Promise<string> {
    return this.http
      .get(`https://restcountries.com/v2/alpha/${countryCode}`)
      .toPromise()
      .then((response: any) => response.borders.join(', '));
  }
}
